    public Map (Context context){

        bitmap = BitmapFactory.decodeResource(context.getResources(), R.drawable.map_tile);

        tileSize = bitmap.getWidth()/imageColCount;

        for (int i=0; i<imageColCount; i++ ){
            for (int j=0; j<imageRowCount; j++){
                imageArray[i*imageRowCount+j+1] = Bitmap.createBitmap(bitmap, i*tileSize, j*tileSize, tileSize, tileSize);
            }
        }

    }

    public int GetCoordX (int col){

        return col * tileSize;
    }

    public int GetCoordY (int row){
        return row * tileSize;
    }

    public Bitmap getBitmap(int col, int row) {

        return imageArray[MapArray[row][col]];
    }

    public int getImageColCount() {
        return imageColCount;
    }

    public int getImageRowCount() {
        return imageRowCount;
    }

    public int getTileSize() {
        return tileSize;
    }

    public int getMapSizeX() {
        return mapSizeX;
    }

    public int getMapSizeY() {
        return mapSizeY;
    }

    public Bitmap[] getImageArray() {
        return imageArray;
    }

    public int[][] getMapArray() {
        return MapArray;
    }

